#include <iostream>
#include <fstream>

using namespace std;

int main(void)
{
	char tmp[] = "";

	__int64 result = 1;
	unsigned int ttmp = 1;
	for (int i = 0; i < sizeof(tmp); i++)
	{
		int temp[5] = { 0 };
		int index = 0;
		for (int j = i; j < i + 5; j++)
		{
			if (tmp[j] - 48 != 0)
			{
				temp[index] = tmp[j] - 48;
				index++;

			}
			else
			{
				break;
			}

		}
		ttmp = 1;
		for (int i = 0; i <= 4; i++)
		{
			ttmp = temp[i] * ttmp;
		}
		printf("%d +%d +%d +%d +%d 결과는 %d <> %d\n", temp[0], temp[1], temp[2], temp[3], temp[4], ttmp, temp[0] + temp[1] + temp[2] + temp[3] + temp[4]);
		if (ttmp>result)
		{
			result = ttmp;
		}


		printf("결과는 %d\n", result);
	}
	printf("end\n");
}
